import React, { useState } from 'react';
import ConfirmationPopup from './ConfirmationPopup';
import axios from 'axios';

function PromptInterface() {
  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');
  const [confirmation, setConfirmation] = useState(null);

  const handlePrompt = async () => {
    const res = await axios.post('https://<your-api-id>.execute-api.us-east-1.amazonaws.com/prod/invocations', {
      prompt
    });
    const data = res.data;
    if (data.confirmation) {
      setConfirmation(data.confirmation);
    } else {
      setResponse(data.message || JSON.stringify(data));
    }
  };

  const handleConfirmation = async (confirmed) => {
    setConfirmation(null);
    if (confirmed) {
      const res = await axios.post('https://<your-api-id>.execute-api.us-east-1.amazonaws.com/prod/invocations', {
        prompt: 'yes'
      });
      setResponse(res.data.message || JSON.stringify(res.data));
    } else {
      setResponse("I did not create anything.");
    }
  };

  return (
    <div>
      <h2>Enter Prompt</h2>
      <input value={prompt} onChange={e => setPrompt(e.target.value)} />
      <button onClick={handlePrompt}>Submit</button>
      <p>Response: {response}</p>
      {confirmation && (
        <ConfirmationPopup
          message={confirmation}
          onConfirm={() => handleConfirmation(true)}
          onCancel={() => handleConfirmation(false)}
        />
      )}
    </div>
  );
}

export default PromptInterface;
